/* tabelle da creare:
	una per ospitare i consolati ministeriali
	una per ospitare i consolati del database locale
	una per il nome del Cliente
	due di raccordo per l'aggiornamento della tabella che ospita i consolati del database locale
*/

create database consolati;

use consolati;

create table consola /*contiene i consolati della tabella locale*/
(
	CodiceCons int(7),
	Consolato varchar(50) NOT NULL,
	Presso varchar(100) not null,
	AltreInfo varchar(50) not null,
	ZipCode varchar (8) not null,
	IstatComune double (10,0) unsigned not null default 0,
	CodMinisteriale int (7) unsigned not null default 0,
	DInizioValidita date not null default '0000-00-00',
	DFineValidita date not null default '0000-00-00',
	primary key (CodiceCons)
);

create table cliente /* tabella dove verr√† inserito il nome del Cliente per cui si sta eseguendo l'allineamento */
(
cliente varchar(60),
primary key(cliente)
);


create table ConsolatoComuneSede /* tabella di raccordo per l'aggiornamento del campo consolato della tabella consola */
(
	CodiceCons int(7),
	Consolato varchar(50),
	ComuneSede varchar(30),
	primary key (CodiceCons)
);

create table AltreInfoRango /* tabella di raccordo per l'aggiornamento del campo AltreInfo della tabella consola */
(
	CodiceCons int(7),
	AltreInfo varchar(50),
	Rango varchar(30),
	primary key (CodiceCons)
);

create table DaInserire
(
	CodiceConsolato int(7),
	CodStatoAppartenenza int (3),
	StatoAppartenenza varchar(40),
	ComuneSede varchar(30),
	Rango varchar(30),
	Indirizzo varchar(200),
	primary key (CodiceConsolato)
);

create table DaAggiornare
(
	CodiceCons int(7),
	Consolato varchar(50) NOT NULL,
	Presso varchar(100) not null,
	AltreInfo varchar(50) not null,
	ZipCode varchar (8) not null,
	IstatComune double (10,0) unsigned not null default 0,
	CodMinisteriale int (7) unsigned not null default 0,
	DInizioValidita date not null default '0000-00-00',
	DFineValidita date not null default '0000-00-00',
	primary key (CodiceCons)
);

create table ConsolatiMinistero /* contiene i consolati ministeriali */
( 
	CodiceConsolato int(7),
	CodStatoAppartenenza int (3),
	StatoAppartenenza varchar(40),
	ComuneSede varchar(30),
	Rango varchar(30),
	Indirizzo varchar(200),
	primary key (CodiceConsolato)
);

